import React, { useEffect } from 'react';
import '../../CSS/Login.css';
import FacebookRoundedIcon from '@mui/icons-material/FacebookRounded';
import GoogleIcon from '@mui/icons-material/Google';
import LinkedInIcon from '@mui/icons-material/LinkedIn';

function SignUp() {
  useEffect(() => {
    const signUpButton = document.getElementById('signUp');
    const signInButton = document.getElementById('signIn');
    const main = document.getElementById('main');

    signUpButton.addEventListener('click', () => {
      main.classList.add('right-panel-active');
    });

    signInButton.addEventListener('click', () => {
      main.classList.remove('right-panel-active');
    });

    return () => {
      signUpButton.removeEventListener('click', () => {
        main.classList.add('right-panel-active');
      });

      signInButton.removeEventListener('click', () => {
        main.classList.remove('right-panel-active');
      });
    };
  }, []);

  async function authenticateUser(email, password) {
    try {
      const response = await fetch('https://your-authentication-api.com/authenticate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        const token = data.token;
        localStorage.setItem('token', token);
        // Redirect or perform any actions after successful authentication
      } else {
        const errorMessage = data.message;
        // Display an error message to the user or handle the error in any way
      }
    } catch (error) {
      console.error('Authentication failed:', error);
    }
  }

  const handleSignUpSubmit = (event) => {
    event.preventDefault();

    const name = event.target.txt.value;
    const email = event.target.email.value;
    const password = event.target.pswd.value;

    // Perform sign-up logic here if needed

    // Call authentication function
    authenticateUser(email, password);
  };

  const handleSignInSubmit = (event) => {
    event.preventDefault();

    const email = event.target.email.value;
    const password = event.target.pswd.value;

    authenticateUser(email, password);
  };

  return (
    <div className="a_container" id="main">
      <div className="a_sign-up">
        <form action="#" onSubmit={handleSignUpSubmit}>
          {/* Rest of the sign-up form */}
        </form>
      </div>
      <div className="a_sign-in">
        <form action="#" onSubmit={handleSignInSubmit}>
          {/* Rest of the sign-in form */}
        </form>
      </div>
      {/* Rest of the code */}
    </div>
  );
}

export default SignUp;
