import React, { useState } from 'react';
import '../../CSS/About.css';

const About = (props) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const descriptionLines = props.des.split('\n');

  const handleCardFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div className={`container ${isFlipped ? 'flipped' : ''}`} onMouseEnter={handleCardFlip} onMouseLeave={handleCardFlip}>
      <div className="front">
        <img src={props.image} className="image" alt="Profile" />
        <h2>{props.name}</h2>
        
      </div>
      <div className="back">
        {descriptionLines.map((line, index) => (
          <p key={index}>{line}</p>
        ))}
      </div>
    </div>
  );
};

export default About;
