    //     }
    //    fetchItems();
    // },[url])
    // const setIndex=(alpha)=>{
    //     setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?f=${alpha}`)
    // }
    // const searchRecipe=(ev)=>{
    //     if(ev.key=="Enter")
    //         setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?s=${search}`)
    // }

    // //Get Current Items
    // const indexOfLastItem =currentPage*itemsPerPage;
    // const indexOfFirstItem=indexOfLastItem-itemsPerPage;
    // const currentItems=item.slice(indexOfFirstItem,indexOfLastItem);

    // //Change Page
    // const paginate=(pageNumber)=>{
    //     setCurrentPage(pageNumber);
    //     }
        
     
    //Pagination
    const getData = async () => {
        setUrl("http://localhost:3008/meals");
        // if (search) {
        //     var url1=url;
        //     url1 += `&q=${search}`;
        //     setUrl(url1);
        // }
        
        fetch(url)
        .then(data => data.json())
        .then(items => {
        console.log(items);
        // setItem(items);

        });
        };


        const handlePageChange = (page) => {
            setCurrentPage(page);}

        useEffect(() => {
            getData();
            }, [currentPage]);


        const handleLogic=(item,el)=>{
            handlePageChange(el)
            const startIndex=el-1;
            const endIndex=el*itemsPerPage;
            const newData=item.slice(startIndex,endIndex);
            setItem(newData);
        }

  return (
    <>
    <div className="main">
        <div className="heading">
            <h1>Search Your Food Recipe</h1>
            <h4>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quaerat assumenda tempore a eos minima? Velit temporibus, ab fuga eos impedit tempore enim quidem repudiandae modi saepe recusandae, ea, incidunt quis. Mollitia, laboriosam odit. Voluptatum.</h4>
        </div>
        {/* <div className='searchBox'>
            <input type="search" className="search-bar" onChange={e=>setSearch(e.target.value)} onKeyDown={searchRecipe}/>
            <button><ImSearch size={15}/></button>
        </div> */}
        <div className="container">
            
            {
                <MealItem data={item} />
            }
        </div>
        <div className="Pagination">
            <Pagination itemsPerPage={itemsPerPage} totalItems={item.length} paginate={paginate} hl={handleLogic}/>
        </div>
        {/* <div className='indexContainer'>
            <RecipeIndex alphaIndex={(alpha)=>setIndex(alpha)}/>
        </div> */}
        


        <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => (
            <button
                key={index + 1}
                id='pagination_button'
                onClick={() => handlePageChange(index + 1)}
                className={currentPage === index + 1 ? "active" : "pageNumber"}
            >
                {index + 1}
            </button>
            ))}
        </div>
            </div>
    </>
  )
        }

export default Meal
