import React, { useState } from 'react';
import '../../CSS/About.css';

const About = (props) => {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleCardFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div className={`s_container ${isFlipped ? 's_flipped' : ''}`} onMouseEnter={handleCardFlip} onMouseLeave={handleCardFlip}>
      <div className="s_front">
        <img src={props.image} className="s_image" alt="Profile" />
        <h2>{props.name}</h2>
        
      </div>
      <div className="back">
        <p>{props.des}</p>
      </div>
    </div>
  );
};

export default About;
